/* Global Styles */
body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    margin: 0;
    padding: 0;
}

.container {
    width: 80%;
    margin: auto;
    overflow: hidden;
    padding: 0 20px;
}

/* Navigation */
nav {
    background: #333;
    color: #fff;
    padding: 10px 0;
}

nav ul {
    padding: 0;
    list-style: none;
}

nav li {
    display: inline;
    padding: 0 20px;
}

nav a {
    color: #fff;
    text-decoration: none;
}

/* Forms */
form {
    margin: 30px 0;
}

input[type="text"],
input[type="email"],
input[type="password"],
textarea {
    width: 100%;
    padding: 10px;
    margin-bottom: 10px;
}

input[type="submit"] {
    background: #333;
    color: #fff;
    padding: 10px 15px;
    border: none;
    cursor: pointer;
}

/* Job Listings */
.job-listing {
    background: #f4f4f4;
    margin: 20px 0;
    padding: 20px;
}

.job-listing h2 {
    margin-top: 0;
}

/* Responsive Design */
@media(max-width: 768px) {
    .container {
        width: 95%;
    }
}

document.addEventListener('DOMContentLoaded', (event) => {
    // Function to fetch and display latest jobs
    const fetchLatestJobs = async () => {
        try {
            const response = await fetch('/api/jobs');
            const jobs = await response.json();
            const jobList = document.getElementById('latest-jobs');

            if (jobList) {
                jobList.innerHTML = '';
                jobs.forEach(job => {
                    const jobElement = document.createElement('div');
                    jobElement.classList.add('job-listing');
                    jobElement.innerHTML = `
                        <h3>${job.title}</h3>
                        <p>Location: ${job.location}</p>
                        <p>Salary: ${job.salary}</p>
                        <p>Posted: ${new Date(job.created_at).toLocaleDateString()}</p>
                        <a href="/job/${job.id}">View Details</a>
                    `;
                    jobList.appendChild(jobElement);
                });
            }
        } catch (error) {
            console.error('Error fetching latest jobs:', error);
        }
    };

    // Call fetchLatestJobs on page load
    fetchLatestJobs();

    // Set up search functionality
    const searchForm = document.getElementById('search-form');
    if (searchForm) {
        searchForm.addEventListener('submit', (e) => {
            e.preventDefault();
            const searchQuery = document.getElementById('search-input').value;
            window.location.href = `/search?q=${encodeURIComponent(searchQuery)}`;
        });
    }

    // Set up language switcher
    const languageSwitcher = document.getElementById('language-switcher');
    if (languageSwitcher) {
        languageSwitcher.addEventListener('change', (e) => {
            const selectedLanguage = e.target.value;
            document.cookie = `language=${selectedLanguage}; path=/`;
            window.location.reload();
        });
    }
});

