/* Global Styles */
body {
    font-family: Arial, sans-serif;
    line-height: 1.6;
    margin: 0;
    padding: 0;
}

.container {
    width: 80%;
    margin: auto;
    overflow: hidden;
    padding: 0 20px;
}

/* Navigation */
nav {
    background: #333;
    color: #fff;
    padding: 10px 0;
}

nav ul {
    padding: 0;
    list-style: none;
}

nav li {
    display: inline;
    padding: 0 20px;
}

nav a {
    color: #fff;
    text-decoration: none;
}

/* Forms */
form {
    margin: 30px 0;
}

input[type="text"],
input[type="email"],
input[type="password"],
textarea {
    width: 100%;
    padding: 10px;
    margin-bottom: 10px;
}

input[type="submit"] {
    background: #333;
    color: #fff;
    padding: 10px 15px;
    border: none;
    cursor: pointer;
}

/* Job Listings */
.job-listing {
    background: #f4f4f4;
    margin: 20px 0;
    padding: 20px;
}

.job-listing h2 {
    margin-top: 0;
}

/* Responsive Design */
@media(max-width: 768px) {
    .container {
        width: 95%;
    }
}

document.addEventListener('DOMContentLoaded', (event) => {
    // Function to fetch and display latest jobs
    const fetchLatestJobs = async () => {
        try {
            const response = await fetch('/api/jobs');
            const jobs = await response.json();
            const jobList = document.getElementById('latest-jobs');

            if (jobList) {
                jobList.innerHTML = '';
                jobs.forEach(job => {
                    const jobElement = document.createElement('div');
                    jobElement.classList.add('job-listing');
                    jobElement.innerHTML = `
                        <h3>${job.title}</h3>
                        <p>Location: ${job.location}</p>
                        <p>Salary: ${job.salary}</p>
                        <p>Posted: ${new Date(job.created_at).toLocaleDateString()}</p>
                        <a href="/job/${job.id}">View Details</a>
                    `;
                    jobList.appendChild(jobElement);
                });
            }
        } catch (error) {
            console.error('Error fetching latest jobs:', error);
        }
    };

    // Call fetchLatestJobs on page load
    fetchLatestJobs();

    // Set up search functionality
    const searchForm = document.getElementById('search-form');
    if (searchForm) {
        searchForm.addEventListener('submit', (e) => {
            e.preventDefault();
            const searchQuery = document.getElementById('search-input').value;
            window.location.href = `/search?q=${encodeURIComponent(searchQuery)}`;
        });
    }

    // Set up language switcher
    const languageSwitcher = document.getElementById('language-switcher');
    if (languageSwitcher) {
        languageSwitcher.addEventListener('change', (e) => {
            const selectedLanguage = e.target.value;
            document.cookie = `language=${selectedLanguage}; path=/`;
            window.location.reload();
        });
    }
});

{% extends "base.html" %}

{% block content %}
    <h1>{{ _('Sign In') }}</h1>
    <form action="" method="post" novalidate>
        {{ form.hidden_tag() }}
        <p>
            {{ form.username.label }}<br>
            {{ form.username(size=32) }}
        </p>
        <p>
            {{ form.password.label }}<br>
            {{ form.password(size=32) }}
        </p>
        <p>{{ form.remember_me() }} {{ form.remember_me.label }}</p>
        <p>{{ form.submit() }}</p>
    </form>
    <p>{{ _('New User?') }} <a href="{{ url_for('main.register') }}">{{ _('Click to Register!') }}</a></p>
{% endblock %}

{% extends "base.html" %}

{% block content %}
    <h1>{{ job.title }}</h1>
    <p>{{ _('Location') }}: {{ job.location }}</p>
    <p>{{ _('Salary') }}: {{ job.salary }}</p>
    <p>{{ _('Posted') }}: {{ job.created_at.strftime('%Y-%m-%d') }}</p>
    <h2>{{ _('Job Description') }}</h2>
    <p>{{ job.description }}</p>

    {% if current_user.is_authenticated and not current_user.is_employer %}
        <a href="{{ url_for('main.apply_job', job_id=job.id) }}" class="btn">{{ _('Apply Now') }}</a>
    {% endif %}

    {% if current_user.is_authenticated and current_user == job.employer %}
        <a href="{{ url_for('main.edit_job', job_id=job.id) }}" class="btn">{{ _('Edit Job') }}</a>
    {% endif %}
{% endblock %}

{% extends "base.html" %}

{% block content %}
    <h1>{{ _('Employer Dashboard') }}</h1>
    <a href="{{ url_for('main.new_job') }}" class="btn">{{ _('Post New Job') }}</a>

    <h2>{{ _('Your Job Listings') }}</h2>
    {% for job in jobs %}
        <div class="job-listing">
            <h3>{{ job.title }}</h3>
            <p>{{ _('Location') }}: {{ job.location }}</p>
            <p>{{ _('Salary') }}: {{ job.salary }}</p>
            <p>{{ _('Posted') }}: {{ job.created_at.strftime('%Y-%m-%d') }}</p>
            <a href="{{ url_for('main.job_details', job_id=job.id) }}">{{ _('View Details') }}</a>
            <a href="{{ url_for('main.edit_job', job_id=job.id) }}">{{ _('Edit') }}</a>
        </div>
    {% endfor %}
{% endblock %}

{% extends "base.html" %}

{% block content %}
    <h1>{{ _('Welcome to Ticino Jobs') }}</h1>
    <p>{{ _('Find your dream job in the Ticino region of Switzerland.') }}</p>

    <form method="POST" action="{{ url_for('main.index') }}">
        {{ form.hidden_tag() }}
        <div>
            {{ form.keyword.label }} {{ form.keyword() }}
            {{ form.location.label }} {{ form.location() }}
        </div>
        <div>
            {{ form.salary_min.label }} {{ form.salary_min() }}
            {{ form.salary_max.label }} {{ form.salary_max() }}
        </div>
        <div>
            {{ form.company_size.label }} {{ form.company_size() }}
        </div>
        {{ form.submit() }}
    </form>

    <h2>{{ _('Latest Job Listings') }}</h2>
    <div id="latest-jobs">
        <!-- Latest jobs will be dynamically populated here -->
    </div>
{% endblock %}

{% extends "base.html" %}

{% block content %}
    <h1>{{ _('User Profile') }}</h1>
    <form action="" method="post">
        {{ form.hidden_tag() }}
        <p>
            {{ form.full_name.label }}<br>
            {{ form.full_name(size=32) }}
        </p>
        <p>
            {{ form.location.label }}<br>
            {{ form.location(size=32) }}
        </p>
        <p>
            {{ form.bio.label }}<br>
            {{ form.bio(rows=4, cols=50) }}
        </p>
        <p>
            {{ form.skills.label }}<br>
            {{ form.skills(size=64) }}
        </p>
        <p>
            {{ form.desired_salary.label }}<br>
            {{ form.desired_salary() }}
        </p>
        <p>
            {{ form.preferred_company_size.label }}<br>
            {{ form.preferred_company_size() }}
        </p>
        <p>{{ form.submit() }}</p>
    </form>

    <h2>{{ _('Recommended Jobs') }}</h2>
    {% if recommended_jobs %}
        {% for job, score in recommended_jobs %}
            <div class="job-listing">
                <h3>{{ job.title }} ({{ _('Match Score') }}: {{ score }})</h3>
                <p>{{ _('Location') }}: {{ job.location }}</p>
                <p>{{ _('Salary Range') }}: {{ job.salary_min }} - {{ job.salary_max }}</p>
                <p>{{ _('Company Size') }}: {{ job.company_size }}</p>
                <a href="{{ url_for('main.job_details', job_id=job.id) }}">{{ _('View Details') }}</a>
            </div>
        {% endfor %}
    {% else %}
        <p>{{ _('No recommended jobs at the moment. Please update your profile to get better recommendations.') }}</p>
    {% endif %}
{% endblock %}

