from flask import Blueprint, render_template, redirect, url_for, flash, request, jsonify
from flask_login import login_user, logout_user, login_required, current_user
from flask_babel import _
from app import db
from app.models import User, Job, Application
from app.forms import LoginForm, RegistrationForm, JobForm, ApplicationForm
from app.utils import save_picture

bp = Blueprint('main', __name__)

@bp.route('/')
@bp.route('/index')
def index():
    return render_template('index.html', title=_('Home'))

@bp.route('/login', methods=['GET', 'POST'])
def login():
    if current_user.is_authenticated:
        return redirect(url_for('main.index'))
    form = LoginForm()
    if form.validate_on_submit():
        user = User.query.filter_by(username=form.username.data).first()
        if user is None or not user.check_password(form.password.data):
            flash(_('Invalid username or password'))
            return redirect(url_for('main.login'))
        login_user(user, remember=form.remember_me.data)
        return redirect(url_for('main.index'))
    return render_template('login.html', title=_('Sign In'), form=form)

@bp.route('/logout')
def logout():
    logout_user()
    return redirect(url_for('main.index'))

@bp.route('/register', methods=['GET', 'POST'])
def register():
    if current_user.is_authenticated:
        return redirect(url_for('main.index'))
    form = RegistrationForm()
    if form.validate_on_submit():
        user = User(username=form.username.data, email=form.email.data, is_employer=form.is_employer.data)
        user.set_password(form.password.data)
        db.session.add(user)
        db.session.commit()
        flash(_('Congratulations, you are now a registered user!'))
        return redirect(url_for('main.login'))
    return render_template('register.html', title=_('Register'), form=form)

@bp.route('/profile', methods=['GET', 'POST'])
@login_required
def profile():
    form = ProfileForm()
    if form.validate_on_submit():
        current_user.profile.full_name = form.full_name.data
        current_user.profile.location = form.location.data
        current_user.profile.bio = form.bio.data
        if form.picture.data:
            picture_file = save_picture(form.picture.data)
            current_user.profile.image_file = picture_file
        db.session.commit()
        flash(_('Your profile has been updated!'))
        return redirect(url_for('main.profile'))
    elif request.method == 'GET':
        form.full_name.data = current_user.profile.full_name
        form.location.data = current_user.profile.location
        form.bio.data = current_user.profile.bio
    return render_template('profile.html', title=_('Profile'), form=form)

@bp.route('/job/new', methods=['GET', 'POST'])
@login_required
def new_job():
    if not current_user.is_employer:
        flash(_('Only employers can post jobs.'))
        return redirect(url_for('main.index'))
    form = JobForm()
    if form.validate_on_submit():
        job = Job(title=form.title.data, description=form.description.data,
                  location=form.location.data, salary=form.salary.data,
                  employer=current_user)
        db.session.add(job)
        db.session.commit()
        flash(_('Your job has been posted!'))
        return redirect(url_for('main.job_listing'))
    return render_template('job_form.html', title=_('Post a New Job'), form=form)

@bp.route('/jobs')
def job_listing():
    page = request.args.get('page', 1, type=int)
    jobs = Job.query.order_by(Job.created_at.desc()).paginate(page=page, per_page=10)
    return render_template('job_listing.html', title=_('Job Listings'), jobs=jobs)

@bp.route('/job/<int:job_id>')
def job_details(job_id):
    job = Job.query.get_or_404(job_id)
    return render_template('job_details.html', title=job.title, job=job)

@bp.route('/job/<int:job_id>/apply', methods=['GET', 'POST'])
@login_required
def apply_job(job_id):
    job = Job.query.get_or_404(job_id)
    if current_user.is_employer:
        flash(_('Employers cannot apply for jobs.'))
        return redirect(url_for('main.job_details', job_id=job.id))
    form = ApplicationForm()
    if form.validate_on_submit():
        application = Application(job=job, applicant=current_user)
        db.session.add(application)
        db.session.commit()
        flash(_('Your application has been submitted.'))
        return redirect(url_for('main.job_details', job_id=job.id))
    return render_template('apply_job.html', title=_('Apply for Job'), form=form, job=job)

@bp.route('/employer/dashboard')
@login_required
def employer_dashboard():
    if not current_user.is_employer:
        flash(_('Only employers can access the dashboard.'))
        return redirect(url_for('main.index'))
    jobs = current_user.jobs.order_by(Job.created_at.desc()).all()
    return render_template('employer_dashboard.html', title=_('Employer Dashboard'), jobs=jobs)

@bp.route('/search')
def search():
    query = request.args.get('q')
    jobs = Job.query.filter(Job.title.contains(query) | Job.description.contains(query)).all()
    return render_template('search_results.html', title=_('Search Results'), jobs=jobs, query=query)

@bp.route('/api/jobs')
def api_jobs():
    jobs = Job.query.order_by(Job.created_at.desc()).limit(10).all()
    return jsonify([{
        'id': job.id,
        'title': job.title,
        'location': job.location,
        'salary': job.salary,
        'created_at': job.created_at.isoformat()
    } for job in jobs])

