        jobs = Job.query.filter(and_(*filters)).order_by(Job.created_at.desc()).paginate(page=page, per_page=10)
    else:
        jobs = Job.query.order_by(Job.created_at.desc()).paginate(page=page, per_page=10)

    recommended_jobs = []
    if current_user.is_authenticated and not current_user.is_employer:
        recommended_jobs = get_job_recommendations(current_user)[:5]  # Get top 5 recommendations

    return render_template('job_listing.html', title=_('Job Listings'), jobs=jobs, form=form, recommended_jobs=recommended_jobs)

